={{ (() => {
  const promptData = $node['Get Questions Prompt'].json.prompt || {};
  const topicData = $node['Prepare Initial Questions'].json || {};

  // Build system message
  const systemParts = [];
  if (promptData.system_prompt) systemParts.push(promptData.system_prompt);
  if (promptData.task) systemParts.push('Task: ' + promptData.task);
  if (promptData.description) systemParts.push('Description: ' + promptData.description);
  if (promptData.current_date) systemParts.push('Current Date: ' + promptData.current_date);
  if (promptData.date_context) systemParts.push('Date Context: ' + promptData.date_context);

  // Build user message
  const userParts = ['/think'];
  if (topicData.topic) userParts.push('Topic: ' + topicData.topic);

  // Add requirements
  if (promptData.requirements && Array.isArray(promptData.requirements) && promptData.requirements.length > 0) {
    userParts.push('');
    userParts.push('## Requirements:');
    userParts.push(promptData.requirements.map(r => '- ' + r).join('\n'));
  }

  // Add tool usage
  if (promptData.tool_usage) {
    userParts.push('');
    userParts.push('## Tool Usage:');
    if (promptData.tool_usage.description) userParts.push('Description: ' + promptData.tool_usage.description);
    if (promptData.tool_usage.functions) userParts.push('Functions Available: ' + JSON.stringify(promptData.tool_usage.functions, null, 2));
    if (promptData.tool_usage.rules && Array.isArray(promptData.tool_usage.rules)) {
      userParts.push('Rules:');
      userParts.push(promptData.tool_usage.rules.map(r => '- ' + r).join('\n'));
    }
  }

  // Add output format
  if (promptData.output_format) {
    userParts.push('');
    userParts.push('## Expected Output Format:');
    userParts.push(JSON.stringify(promptData.output_format, null, 2));
  }

  // Add ALL examples in JSON format
  if (promptData.example_outputs && Array.isArray(promptData.example_outputs) && promptData.example_outputs.length > 0) {
    userParts.push('');
    userParts.push('## Example Outputs (ALL examples in JSON):');
    userParts.push(JSON.stringify(promptData.example_outputs, null, 2));
  }

  // Add inputs section if present
  if (promptData.inputs) {
    userParts.push('');
    userParts.push('## Inputs Template:');
    userParts.push(JSON.stringify(promptData.inputs, null, 2));
  }

  // Add current date and date context
  if (promptData.current_date) {
    userParts.push('');
    userParts.push('## Current Date: ' + promptData.current_date);
  }
  if (promptData.date_context) {
    userParts.push(promptData.date_context);
  }

  // Add input parameters
  userParts.push('');
  userParts.push('## Input Parameters:');
  userParts.push('Style: ' + (topicData.style || 'investigative journalism'));
  userParts.push('Additional Requirements: ' + (topicData.requirements || 'none'));

  // Return the complete request object
  return {
    model: 'Qwen/Qwen3-235B-A22B-Thinking-2507',
    messages: [
      {
        role: 'system',
        content: systemParts.filter(Boolean).join('\n\n') || 'You are an investigative journalist.'
      },
      {
        role: 'user',
        content: userParts.filter(Boolean).join('\n')
      }
    ],
    temperature: 0.7,
    max_tokens: 8000,
    response_format: { type: "json_object" }
  };
})() }}

