={{ (() => {
  const promptData = $node['Get Structure Prompt'].json.prompt || {};
  const questionsNode = $node['Format Final Questions'];
  const searchNode = $node['Process Search Results'];

  // Get data from previous nodes
  const chatId = questionsNode?.json?.chat_id || '';
  const topic = questionsNode?.json?.topic || '';
  const shortName = questionsNode?.json?.short_name || '';
  const questionsData = questionsNode?.json?.questions_data || {};
  const searchResults = searchNode?.json?.search_results || [];
  const searchSummary = searchNode?.json?.search_summary || '';

  // Build system message
  const systemParts = [];
  if (promptData.system_prompt) systemParts.push(promptData.system_prompt);
  if (promptData.task) systemParts.push('Task: ' + promptData.task);
  if (promptData.description) systemParts.push('Description: ' + promptData.description);

  // Build user message
  const userParts = ['/think'];
  userParts.push('Topic: ' + topic);
  userParts.push('Project: ' + shortName);

  // Add research questions
  if (questionsData.research_questions && questionsData.research_questions.length > 0) {
    userParts.push('');
    userParts.push('## Research Questions:');
    questionsData.research_questions.forEach((q, i) => {
      userParts.push(`${i + 1}. ${q}`);
    });
  }

  // Add key angles
  if (questionsData.key_angles && questionsData.key_angles.length > 0) {
    userParts.push('');
    userParts.push('## Key Research Angles:');
    questionsData.key_angles.forEach(angle => {
      userParts.push('‚Ä¢ ' + angle);
    });
  }

  // Add required sources
  if (questionsData.required_sources && questionsData.required_sources.length > 0) {
    userParts.push('');
    userParts.push('## Required Sources:');
    questionsData.required_sources.forEach(source => {
      userParts.push('‚Ä¢ ' + source);
    });
  }

  // Add scope definition
  if (questionsData.scope_definition) {
    userParts.push('');
    userParts.push('## Research Scope:');
    if (questionsData.scope_definition.geographic_focus) {
      userParts.push('Geographic Focus: ' + questionsData.scope_definition.geographic_focus);
    }
    if (questionsData.scope_definition.time_period) {
      userParts.push('Time Period: ' + questionsData.scope_definition.time_period);
    }
    if (questionsData.scope_definition.depth) {
      userParts.push('Depth: ' + questionsData.scope_definition.depth);
    }
  }

  // Add search results summary
  if (searchSummary) {
    userParts.push('');
    userParts.push('## Available Sources from Initial Search:');
    userParts.push(searchSummary);
  }

  // Add instructions from prompt
  if (promptData.instructions) {
    userParts.push('');
    userParts.push('## Instructions:');
    if (promptData.instructions.primary_task) {
      userParts.push('Primary Task: ' + promptData.instructions.primary_task);
    }
    if (promptData.instructions.requirements && Array.isArray(promptData.instructions.requirements)) {
      userParts.push('');
      userParts.push('Requirements:');
      promptData.instructions.requirements.forEach(req => {
        userParts.push('- ' + req);
      });
    }
    if (promptData.instructions.structure_elements) {
      userParts.push('');
      userParts.push('Structure Elements:');
      userParts.push(JSON.stringify(promptData.instructions.structure_elements, null, 2));
    }
  }

  // Add output format
  if (promptData.output_format) {
    userParts.push('');
    userParts.push('## Expected Output Format:');
    userParts.push(JSON.stringify(promptData.output_format, null, 2));
  }

  // Add examples if present
  if (promptData.examples && Array.isArray(promptData.examples) && promptData.examples.length > 0) {
    userParts.push('');
    userParts.push('## Example Structure:');
    userParts.push(JSON.stringify(promptData.examples[0], null, 2));
  }

  // Add character limit
  userParts.push('');
  userParts.push('## Target Article Length:');
  userParts.push('Approximately 25,000 characters (comprehensive journalistic investigation)');

  // Return the complete request object for HuggingFace
  return {
    model: 'Qwen/Qwen3-235B-A22B-Thinking-2507',
    messages: [
      {
        role: 'system',
        content: systemParts.filter(Boolean).join('\n\n') || 'You are a senior editor specializing in article structure.'
      },
      {
        role: 'user',
        content: userParts.filter(Boolean).join('\n')
      }
    ],
    temperature: 0.7,
    max_tokens: 8000,
    response_format: { type: "json_object" }
  };
})() }}

